mapper.select:
SELECT p.*
FROM patients p
WHERE p.patient_no = #{patientNo, jdbcType=INTEGER}

SELECT p.*
FROM patients p

     AND UPPER(p.patient_no) LIKE UPPER(CONCAT(#{patientNo},'%'))
     AND UPPER(p.first_name) LIKE UPPER(CONCAT(#{firstName},'%'))
     AND UPPER(p.last_name) LIKE UPPER(CONCAT(#{lastName},'%'))
     AND UPPER(p.fathers_name) LIKE UPPER(CONCAT(#{fathersName},'%'))
     AND UPPER(p.mothers_name) LIKE UPPER(CONCAT(#{mothersName},'%'))
     AND p.birth_date = #{birthDate}
     AND p.gender_code = #{genderCode}
     AND p.marital_status_code = #{maritalStatusCode}
     AND p.nationality_code = #{nationalityCode}
     AND UPPER(p.phone_number) like UPPER(CONCAT(#{phoneNumber},'%'))

mapper.update:
UPDATE patients
first_name= #{firstName, jdbcType=VARCHAR}, last_name = #{lastName, jdbcType=VARCHAR}, fathers_name= #{fathersName, jdbcType=VARCHAR}, mothers_name= #{mothersName, jdbcType=VARCHAR}, birth_date = #{birthDate, jdbcType=DATE}, gender_code= #{genderCode, jdbcType=VARCHAR}, marital_status_code= #{maritalStatusCode, jdbcType=VARCHAR}, nationality_code= #{nationalityCode, jdbcType=VARCHAR}, phone_number= #{phoneNumber, jdbcType=VARCHAR},
WHERE patient_no= #{patientNo, jdbcType=INTEGER}
